	public static  byte[] GetBytes(string str)
	{
		return System.Text.Encoding.Unicode.GetBytes(str);
	}

	public static  string GetString(byte[] bytes)
	{
		return System.Text.Encoding.Unicode.GetString(bytes);
	}

	private byte[]Base64ToTexter2d(string Base64STR)  
	{  
		return System.Convert.FromBase64String (Base64STR);  
	}


	public  Texture2D Base64StringToTexture2D(string base64)
	{
		Texture2D tex = new Texture2D (4, 4, TextureFormat.ARGB32, false);
		try
		{
			byte[] bytes = System.Convert.FromBase64String(base64);
			tex.LoadImage(bytes);
		}
		catch(System.Exception ex)
		{
			Debug.LogError(ex.Message);
		}
		return tex;
	}    

